#include <gtest/gtest.h>
#include <cnperf_api.h>

TEST(CnperfApiTypeTest, DeviceTaskTypeValues) {
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_KERNEL, 0);
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_ATOMIC, 1);
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_HOST_FUNC, 2);
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_MEMCPY, 3);
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_MEMORY, 4);
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_MEMSET, 5);
  EXPECT_EQ(CNPERF_DATA_DEVICE_TASK_NOTIFIER, 6);
  // TODO: Add COUNT value for cnperfDataDeviceTaskType_t enum
}

TEST(CnperfApiTypeTest, DataCommTaskTypeValues) {
  EXPECT_EQ(CNPERF_DATA_COMM_TASK_TYPE_UNKNOWN, 0);
  EXPECT_EQ(CNPERF_DATA_COMM_TASK_TYPE_COLLECTIVE, 1);
  EXPECT_EQ(CNPERF_DATA_COMM_TASK_TYPE_P2P, 2);
  // TODO: Add COUNT value for cnperfDataCommTaskType_t enum
}

TEST(CnperfApiTypeTest, DataPmuValueTypeValues) {
  EXPECT_EQ(CNPERF_DATA_PMU_VALUE_TYPE_UINT64, 0);
  EXPECT_EQ(CNPERF_DATA_PMU_VALUE_TYPE_INT64, 1);
  EXPECT_EQ(CNPERF_DATA_PMU_VALUE_TYPE_DOUBLE, 2);
  // TODO: Add COUNT value for cnperfDataPmuValueType_t enum
}
